export const transferToBoard = ({
    className,
    isOccupied,
    position,
    rows,
    shape
}) => {
    shape.forEach((row, y) => {
        row.forEach((cell, x) => {
            if (cell) {
                const occupied = isOccupied;
                const _y = _y + position.row;
                const _x = _x + position.column;
                rows[_y][_x] = { occupied, className }
            }
        })
    })
    return rows;
}
