import { Action } from "./Input";
import { rotate } from "./Tetrominoes"

const attemptRotation = ({ board, player, set})

export const playerController = ({
    action,
    board,
    player,
    setPlayer,
    setGameOver,
}) => {
    if (!action) return;

    if (action === Action.Rotate) {
        attemptRotation({ board, player, setPlayer });
    }
}
